package Missao01;

public class Missao01_DESAFIO3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double x =12.34; // declarando uma variavel do tipo doble
		int y; // declarando uma variavel do tipo int
		
		y =(int) x; // convertendo o dado da variavel x (que é um doble) para int
		
		System.out.println("O valor da variavel x é "+ x);
		System.out.println("O valor da variavel y é "+ y);
		
		System.out.println("Desafio3 concluido");
		

	}

}
