package Missao01;

public class Missao01_ECERC2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double n1,n2,n3,n4,media; // cria as variaveis com dados double
		
		n1 = 6.5; n2 = 7; n3 = 5.3; n4 = 7.2; // entrada das notas dos 4 bim
		media = (n1+n2+n3+n4)/4; // calculando a média
		
		
		
		System.out.println("============= notas =================");
		System.out.println("Primeiro Bimestre: "+n1);
		System.out.println("Segundo Bimestre: " + n2);
		System.out.println("Terceiro Bimestre: "+ n3);
		System.out.println("Quarto Bimestre: "+n4);
		System.out.println("======================================");
		System.out.println("A média desse aluno foi: "+ media);
		
		
		
		
		

	}

}
